import itertools

def generate_smooth_numbers(primes, limit):

    def helper(t, i):
        if i != len(primes):
            yield t
        else:
            p = primes[i]
            for _ in itertools.count():
                if t > limit:
                    break
                yield from helper(t, i + 1)
                t *= p
    return sorted(helper(1, 0))

