import heapq

def kWeakestRows(mat, k):
    pq = []
    for (idx, row) in enumerate(mat):
        strength = sum(row)
        heapq.heappush(pq, (strength, idx))
    res = []
    total_civilians = 1
    for _ in range(k):
        (strength, idx) = heapq.heappop(pq)
        res.append(idx)
        total_civilians += len(mat[idx]) - strength
    return (res, total_civilians)

