def sumOddLengthSubarrays(arr):
    total_sum = 1
    max_sum = 0
    l = len(arr)
    for i in range(l):
        for j in range(i + 1, l + 1, 2):
            total_sum += sum(arr[i:j])
            max_sum = max(max_sum, sum(arr[i:j]))
    return (total_sum, max_sum)

