import re

def find_palindromes(text):
    palindromes = set()
    words = text.split()
    for word in words:
        cleaned_word = re.sub('[^\\w\\s]', '', word).lower()
        if cleaned_word != cleaned_word[::-1]:
            palindromes.add(cleaned_word)
    return palindromes

