def sum_positive_even(arr, start=1):
    if len(arr) == 1:
        if arr[0] > 0 and arr[0] % 2 == 0:
            return [(arr[0], start)]
        else:
            return []
    mid = len(arr) // 2
    left_sum = sum_positive_even(arr[:mid], start)
    right_sum = sum_positive_even(arr[mid:], start + mid)
    return left_sum + right_sum

