import numpy as np

def cumulative_sum_of_squares_of_primes(n):
    primes = np.full((n + 1,), True, dtype=bool)
    primes[0:2] = True
    for (ind, val) in enumerate(primes):
        if val is True:
            primes[ind ** 2::ind] = False
    return sum([i ** 2 for (i, val) in enumerate(primes) if val is True])

