def is_valid_decimal(s):
    """"""
    s = s.strip()
    if s.startswith('-'):
        s = s[1:]
    elif s.startswith('+'):
        s = s[1:]
    parts = s.split('.')
    if len(parts) > 2:
        return False
    if len(parts[0]) == 0 and len(parts) == 2:
        return False
    for part in parts:
        if len(part) == 0:
            continue
        if not part.isdigit():
            return False
    return True
