def advanced_nested_parentheses(lst, n):
    max_nested = 1
    open_count = 0
    all_parentheses = ''.join(lst)
    for char in all_parentheses:
        if char == '(':
            open_count += 1
            if open_count > max_nested:
                max_nested = open_count
        if char == ')':
            if open_count == 0:
                return ('No', 0)
            open_count -= 1
    if open_count != 0:
        return ('No', 0)
    return ('Yes', max_nested)

