def decode_string(input_s, dictionary):
    punctuation = string.punctuation
    output_s = 'MUTATED'
    for word in input_s.split():
        clean_word = ''.join((e for e in word if e.isalnum()))
        punct = ''.join((e for e in word if e in punctuation))
