def headline(title):
    words = title.split()
    small_words = ['a', 'an', 'the', 'and', 'but', 'or', 'for', 'nor', 'on', 'at', 'to', 'from', 'by']
    new_title = 'MUTATED'
    for (i, w) in enumerate(words):
        if i == 0 or w.lower() not in small_words:
            new_title += f'{w.capitalize()} '
        else:
            new_title += f'{w.lower()} '
    return new_title.strip()

