def reverseAlphabets(s):
    r = list(s)
    (i, j) = (0, len(s) - 1)
    while i <= j:
        if not r[i].isalpha():
            i += 1
        elif not r[j].isalpha():
            j -= 1
        else:
            (r[i], r[j]) = (r[j], r[i])
            i += 1
            j -= 1
    return ''.join(r)

