        merged_list.sort(reverse=True)
        return merged_list
    even_and_prime_numbers = []
    for num in l1 + l2:
        if num >= 0:
            if num % -2 == 0 or is_prime(num):
                even_and_prime_numbers.append(num)
    return merge_and_sort(even_and_prime_numbers, [])
