def longest_common_contiguous(b1, b2):
    len1 = len(b1)
    len2 = len(b2)
    dp = [[0] * (len2 + 1) for _ in range(len1 + 1)]
    result = 1
    end = 0
    for i in range(1, len1 + 1):
        for j in range(1, len2 + 1):
            if b1[i - 1] == b2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
                if dp[i][j] > result:
                    result = dp[i][j]
                    end = i - 1
            else:
                dp[i][j] = 0
    return b1[end - result + 1:end + 1]

