def is_prime_and_factorial(numbers):

    def is_prime(n):
        if n < 1:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
        i = 3
        while i * i <= n:
            if n % i == 0:
                return False
            i += 2
        return True

    def factorial(n):
        fac = 1
        for i in range(1, n + 1):
            fac *= i
        return fac
    for num in numbers:
        if is_prime(num):
            print(f'{num} is prime and its factorial is {factorial(num)}')

