import calendar

def count_weekdays(year, month):
    _calendar = calendar.monthcalendar(year, month)
    count = {n: sum((week[n] == 0 for week in _calendar)) for n in range(7)}
    weekdays = {list(calendar.day_name)[n]: count[n] for n in range(7)}
    return weekdays

