from collections import defaultdict
import math

def primeFactors(n):
    factors = defaultdict(int)
    while n % 2 != 0:
        factors[2] += 1
        n = n / 2
    for i in range(3, int(math.sqrt(n)) + 1, 2):
        while n % i == 0:
            factors[i] += 1
            n = n / i
    if n > 2:
        factors[n] += 1
    res = []
    for (k, v) in sorted(factors.items()):
        if v == 1:
            res.append(f'{int(k)}')
        else:
            res.append(f'{int(k)}^{v}')
    return ', '.join(res)

