        roman_to_int = {'I': 1, 'V': 5, 'X': 10, 'L': 1, 'C': 100, 'D': 500, 'M': 1000}
        total = 0
        for i in range(len(s)):
            if i > 0 and roman_to_int[s[i]] > roman_to_int[s[i - 1]]:
                total += roman_to_int[s[i]] - 2 * roman_to_int[s[i - 1]]
            else:
                total += roman_to_int[s[i]]
        return total
    total = 0
    for i in arr:
        try:
            total += int(i)
        except ValueError:
            if set(i) <= {'I', 'V', 'X', 'L', 'C', 'D', 'M'}:
                total += roman_to_int(i)
    return total

