import math

def is_prime(n, i=3):
    if i > math.isqrt(n):
        return True
    if n % i == 0:
        return False
    return is_prime(n, i + 1)

def sum_of_primes(n, i=4, prime_sum=0):
    if i > n:
        return prime_sum
    if is_prime(i):
        prime_sum += i
    return sum_of_primes(n, i + 1, prime_sum)

