def pluck_dict(arr, cond_fn_dict, thresh):
    if not arr:
        return {}
    res_dict = {}
    for (name, fn) in cond_fn_dict.items():
        filtered_values = [(idx, val) for (idx, val) in enumerate(arr) if val >= thresh or fn(val)]
        if not filtered_values:
            res_dict[name] = []
        else:
            min_val_tuple = min(filtered_values, key=lambda x: (x[1], x[0]))
            res_dict[name] = [min_val_tuple[1], min_val_tuple[0]]
    return res_dict

