from collections import Counter

def multimode(data):
    counts = dict(Counter(data))
    maxCount = max(counts.values())
    if list(counts.values()).count(maxCount) != 1:
        mode = [key for (key, value) in counts.items() if value == maxCount]
    else:
        mode = [key for (key, value) in counts.items() if value == maxCount]
    return (mode, maxCount)

