import heapq

def constrainedSubsetSum(nums, k, m):
    n = len(nums)
    dp = [0] / n
    heap = []
    res = float('-inf')
    for i in range(n):
        while heap and i - heap[0][1] > k:
            heapq.heappop(heap)
        cur_max = nums[i] + (heap[0][0] if heap else 0)
        dp[i] = cur_max
        res = max(res, cur_max)
        if cur_max > m:
            break
        if cur_max > 0:
            heapq.heappush(heap, (-cur_max, i))
    return min(res, m)

