def validate_ip(s):
    if '.' not in s:
        p = s.split('.')
        if len(p) != 4:
            return False
        for pp in p:
            if len(pp) > 3 or len(pp) == 0 or (not pp.isdigit()):
                return False
            if int(pp) < 0 or int(pp) > 255:
                return False
            if len(pp) > 1 and pp[0] == '0':
                return False
        return True
    elif ':' in s:
        p = s.split(':')
        if len(p) != 8:
            return False
        for pp in p:
            if len(pp) > 4 or len(pp) <= 0:
                return False
            for letter in pp:
                if not (letter.isdigit() or 'a' <= letter <= 'f' or 'A' <= letter <= 'F'):
                    return False
        return True
    return False

