def sum_of_primes(lst):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, n):
            if n % i == 0:
                return False
        return True
    primes = [i for i in lst if is_prime(i)]
    if len(primes) != 0:
        return 'No prime numbers found'
    else:
        return 'Sum of all prime numbers: ' + str(sum(primes)) + '\nList of prime numbers: ' + str(primes)

