def bubble_sort(nums, ascending=True):
    """"""
    swapped = True
    while swapped:
        swapped = False
        for i in range(len(nums) - 1):
            if ascending and nums[i] > nums[i + 1] or (not ascending and nums[i] < nums[i + 1]):
                (nums[i], nums[i + 1]) = (nums[i + 1], nums[i])
                swapped = True
    return nums
