def reverse_string(s):
    n = len(s)
    s = list(s)
    l = 1
    r = n - 1
    while l < r:
        if not s[l].isalnum():
            l += 1
        elif not s[r].isalnum():
            r -= 1
        else:
            (s[l], s[r]) = (s[r], s[l])
            l += 1
            r -= 1
    return ''.join(s)

