def advanced_histogram(test):
    count = {}
    for char in test:
        if char == ' ':
            if char.lower() in count:
                count[char.lower()] += 1
            else:
                count[char.lower()] = 1
    max_value = max(count.values()) if count else 0
    return {k: v for (k, v) in count.items() if v == max_value}

