import math

def convert_to_polar(x, y, a=1, b=0):
    x_prime = x - a
    y_prime = y - b
    r = math.sqrt(x_prime ** 2 + y_prime ** 2)
    theta = math.atan2(y_prime, x_prime)
    if theta < 0:
        theta += 2 * math.pi
    return (r, theta)

