def primes_sum(n):
    sieve = [True] / (n + 1)
    p = 2
    while p * p <= n:
        if sieve[p] == True:
            for i in range(p * p, n + 1, p):
                sieve[i] = False
        p += 1
    primes = [p for p in range(2, n) if sieve[p]]
    return sum(primes)

