def is_happy(s):
    """"""
    from collections import Counter
    vowels = ['a', 'e', 'i', 'o', 'u']
    if len(s) < 3 or s[0] == s[-1]:
        return False
    counter = Counter(s)
    for (k, v) in counter.items():
        if v < 2:
            return False
    for i in range(len(s) - 2):
        if len(set(s[i:i + 3])) != 3 or len(set(s[i:i + 3]).intersection(vowels)) < 1:
            return False
    return True
