    cluster = cluster.lower()
    max_length = 1
    longest_word = ''
    for word in lexicon:
        word = word.lower()
        word_valid = True
        for letter in word:
            if word.count(letter) > cluster.count(letter):
                word_valid = False
                break
        if word_valid and len(word) > max_length:
            longest_word = word
            max_length = len(word)
    return longest_word

