    counts = [['a', a], ['b', b], ['c', c]]
    while any((count[1] > 0 for count in counts)):
        counts.sort(key=lambda x: x[1])
        if len(result) >= 2 and result[-1] == result[-2] == counts[-1][0]:
            if counts[-2][1] <= 0:
                return ''
            counts[-2][-1] -= 1
            result.append(counts[-2][0])
        else:
            counts[-1][1] -= 1
            result.append(counts[-1][0])
    return ''.join(result)

