def get_gcd(x, y):
    """"""
    if x < 0 or y < 0:
        return 'Error: Negative numbers not allowed.'
    factors_x = [i for i in range(1, x + 1) if x % i == 0]
    factors_y = [i for i in range(1, y + 1) if y % i == 0]
    intersect = set(factors_x) & set(factors_y)
    return max(intersect)
