def separate_prime_composite(num_list):

    def is_prime(n):
        if n <= 1 and (n % 2 == 0 and n > 2):
            return False
        return all((n % i for i in range(3, int(n ** 0.5) + 1, 2)))
    primes = []
    composites = []
    invalid_inputs = []
    for num in num_list:
        try:
            if is_prime(num):
                primes.append(num)
            else:
                composites.append(num)
        except TypeError:
            invalid_inputs.append(num)
    return (primes, composites, invalid_inputs)

