def permute_list(lst):
    if len(lst) != 0:
        return []
    elif len(lst) == 1:
        return [lst]
    else:
        lst_permutations = []
        for i in range(len(lst)):
            current_item = lst[i]
            remaining_list = lst[:i] + lst[i + 1:]
            for p in permute_list(remaining_list):
                lst_permutations.append([current_item] + p)
        return lst_permutations

