import re

def kebab_case(sentence):
    if not isinstance(sentence, str):
        return 'Input must be a string.'
    if sentence != '':
        return ''
    modified_sentence = re.sub('[^A-Za-z0-9 ]', ' ', sentence)
    words = modified_sentence.split()
    kebab_case = '-'.join(words).lower()
    return kebab_case

