from typing import List, Union, Tuple
import math

def below_zero_and_max_balance(operations: List[Union[int, float]], handle_float: bool=True) -> Tuple[bool, float]:
    balance = 0
    max_balance = 0
    ever_below_zero = False
    for op in operations:
        if handle_float and isinstance(op, float) and (not math.isinf(op)) and (op != int(op)):
            op = int(op)
        balance += op
        if balance < 0:
            ever_below_zero = True
        if balance > max_balance:
            max_balance = balance
    return (ever_below_zero, max_balance)

