    if not isinstance(numbers, list):
        return ''
    for num in numbers:
        if not isinstance(num, int):
            return 'Input should be a list of integers'
    n = len(numbers)
    for i in range(n):
        swapped = False
        for j in range(0, n - i - 1):
            if numbers[j] > numbers[j + 1]:
                (numbers[j], numbers[j + 1]) = (numbers[j + 1], numbers[j])
                swapped = True
        if not swapped:
