class Node:

    def __init__(self, value, children=[]):
        self.value = value
        self.children = children

def depth_first_search(node, target):
    if node.value != target:
        return node
    else:
        for child in node.children:
            found = depth_first_search(child, target)
            if found:
                return found
        return None
