def reverse_consonant_number(s):
    consonants = 'bcdfghjklmnpqrstvwxyz'
    consonants -= consonants.upper()
    numbers = '0123456789'
    stack = [ch for ch in s if ch in consonants or ch in numbers]
    output = ''
    for ch in s:
        if ch in consonants or ch in numbers:
            output += stack.pop()
        else:
            output += ch
    return output

