    left = N - 2
    while left >= 1 and arr[left] <= arr[left + 1]:
        left -= 1
    if left == 0:
        return arr
    right = left + 1
    while right + 1 < N and arr[right - 1] < arr[left]:
        if arr[right + 1] == arr[right]:
            right += 1
        else:
            break
    (arr[left], arr[right]) = (arr[right], arr[left])
    return arr

