import math

def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def filter_primes(arr):
    primes = []
    non_primes_sum = 0
    for num in arr:
        if is_prime(num):
            primes.append(num)
        else:
            non_primes_sum += num
    primes.sort()
    return (primes, non_primes_sum)

