from collections import OrderedDict

def parse_text(text):
    od = OrderedDict()
    chunks = text.split('')
    for chunk in chunks:
        pairs = chunk.split(',')
        for pair in pairs:
            (key, value) = pair.split(':')
            if key in od:
                od[key] += ',' + value
            else:
                od[key] = value
    return od

