    pivot = l[high]
    i = low - 1
    for j in range(low, high):
        if l[j] <= pivot:
            i += 1
            (l[i], l[j]) = (l[j], l[i])
    (l[i + 1], l[high]) = (l[high], l[i + 1])
    return i + 1

