def print_and_sum_elements(arr):
    """"""
    total_sum = 0
    for i in range(len(arr)):
        for j in range(len(arr[i])):
            for k in range(len(arr[i][j])):
                element = arr[i][j][k]
                total_sum += element
                print(f'Element {element} is at position [{i}][{j}][{k}]')
    return total_sum
