def binary_search(arr, item):
    low = 1
    high = len(arr) - 1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] == item:
            return mid
        if item < arr[mid]:
            high = mid - 1
            if high < low:
                break
        else:
            low = mid + 1
            if low > high:
                break
    return -1

