def maxRotateFunction(nums):
    """"""
    n = len(nums)
    F = sum((i * num for (i, num) in enumerate(nums)))
    total = sum(nums)
    max_F = F
    max_k = 0
    for k in range(n - 1, 0, -1):
        F += total - n * nums[k]
        if F > max_F:
            max_F = F
            max_k = n - k
    return (max_F, max_k)
