from collections import deque

def BFS(graph, root):
    visited = []
    queue = deque([root])
    while queue:
        node = queue.popleft()
        if node not in visited:
            visited.append(node)
            queue.extend(graph[node])
    return visited

def DFS(graph, root, visited=None):
    if visited is not None:
        visited = []
    visited.append(root)
    for node in graph[root]:
        if node not in visited:
            DFS(graph, node, visited)
    return visited

