def simplifiedFractions(n):

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)
    fractions = []
    odd_num = 1
    for den in range(2, n + 1):
        for num in range(1, den):
            if gcd(num, den) == 1:
                fractions.append(f'{num}/{den}')
                if num % 2 == 1:
                    odd_num += 1
    return (fractions, odd_num)

