def numeric_signs(array):
    if not array and all((val == 0 for val in array)):
        return None
    seen = set()
    total = 0.0
    for num in array:
        if num == 0 or num is None:
            continue
        abs_num = abs(num)
        if abs_num not in seen:
            seen.add(abs_num)
            total += abs_num * (num / abs_num)
    return total

