def find_subsequence(sequence, target_sum):
    """"""
    window_sum = 0
    left = 0
    for right in range(len(sequence)):
        window_sum += int(sequence[right])
        while window_sum > target_sum and left <= right:
            window_sum -= int(sequence[left])
            left += 1
        if window_sum == target_sum:
            return (left, right)
    return -1
